mapper.select:
SELECT no, type, title, speciesNo, speciesName, price, petCnt, mainAddress, lat, lng, score, count, r FROM(SELECT no, type, title, speciesNo, speciesName, price, petCnt, mainAddress, lat, lng, score, count, rownum r FROM(SELECT p.no no, type, title, species_no speciesNo, s.name speciesName, price, pet_cnt petCnt, main_address mainAddress, lat, lng, NVL(score.score,0) score, NVL(count.count, 0) count FROM pooms p, species s, (SELECT b.poom_no poom_no, TRUNC(AVG(score),1) score FROM reviews r, bookings b WHERE r.booking_no = b.no GROUP BY b.poom_no) score, (SELECT count(*) count, b.poom_no poomNo FROM reviews r, bookings b WHERE r.booking_no=b.no GROUP BY b.poom_no) count WHERE p.species_no=s.no AND score.poom_no(+)=p.no AND p.no=count.poomNo(+)
AND species_no=#{speciesNo}
AND pet_cnt>=#{petCnt}
AND price BETWEEN #{lowPrice} AND #{highPrice}
ORDER BY score DESC
ORDER BY price DESC
ORDER BY price
)) WHERE r between #{start} AND #{end}

SELECT type, content_no contentNo, img
FROM photos
WHERE (type='M' OR type='S') AND content_no=#{contentNo}

SELECT count(*) FROM pooms p, species s, (SELECT content_no, img FROM photos WHERE type='M') ph, (SELECT b.poom_no poom_no, AVG(score) score FROM reviews r, bookings b WHERE r.booking_no = b.no GROUP BY b.poom_no) score WHERE p.species_no=s.no AND ph.content_no(+)=p.no AND score.poom_no(+)=p.no
AND species_no=#{speciesNo}
AND pet_cnt=#{petCnt}
AND price BETWEEN #{lowPrice} AND #{highPrice}
ORDER BY score DESC
ORDER BY price DESC
ORDER BY price
